// import logo from './logo.svg';
// import './App.css';
import React, { useState } from "react";
function App() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  const handleLogin = () => {
    let e = ''
    let p = ''
    if (e === "pulu@index.co" && p === "12345") {
      console.log("anda berhasil login")
      alert("anda berhasil login")

    } else if (e === "" || p === "") {
      console.log("belum isi email atau password")
      alert("belum isi email atau password")
    }
    else if (e === "pulu@index.co" || p === "12345") {
      console.log("salah password atau email")
      alert("salah password atau email")
    } else {
      console.log("email dan password salah")
      alert("email dan password salah")
    }
  }
  return (
    <div className="container">
      <div className="form-container">
        <div className="form-box">
          <div className="forms">
            <div className="form-control">
              <label>email</label><span><sup>*</sup></span>
              <input type="email" placeholder="Email" onchange={(e) => setEmail(e.target.value)} id={"email"} />
            </div>
            <div className="form-control">
              <label>Password</label><span><sup>*</sup></span>
              <input type="password" placeholder="Password" id="password" onchange={(e) => setPassword(e.target.value)} />
            </div>
            <div className="form-button">
              <button onClick={() => handleLogin()}>Login</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
